module Prerendered 
    ( prerenderedSign
    , prerenderedPlanet
    , alternateTextPath
    , textPath
    , renderAll
    )
where

import Import
import Diagrams.Prelude
import SwissEphemeris (Planet(..))
import Diagrams.Backend.SVG (renderSVG, B)
import qualified Graphics.SVGFonts as SF
import System.IO.Unsafe (unsafePerformIO)
import qualified Graphics.SVGFonts.ReadFont as SF

-- | Default font, bundled with the SVGFonts package,
-- is linux libertine. It has good support for unicode, but doesn't
-- include the more obscure symbols necessary for e.g. Lilith, Chiron, and
-- some astrological aspects. See `alternateGlyphFont`.
defaultGlyphFont :: SF.PreparedFont Double
defaultGlyphFont = unsafePerformIO $ SF.lin
{-# NOINLINE defaultGlyphFont #-}

-- | Symbola SVG font map created with: https://fontforge.org/en-US/downloads/mac-dl/
-- by downloading the Symbola font and converting (open in FF, convert.)
-- Symbola is used after confirming support for more rare characters at:
-- https://www.fileformat.info/info/unicode/char/26b8/index.htm
alternateGlyphFont :: SF.PreparedFont Double
alternateGlyphFont = unsafePerformIO $ SF.loadFont "static/symbola.svg"
{-# NOINLINE alternateGlyphFont #-}

textPath' :: SF.PreparedFont Double -> String -> Path V2 Double
textPath' font_ = SF.textSVG' (SF.TextOpts font_ SF.INSIDE_H SF.KERN False 1 1)

textPath :: String -> Path V2 Double
textPath = textPath' defaultGlyphFont
alternateTextPath :: String -> Path V2 Double
alternateTextPath = textPath' alternateGlyphFont

-- NOTE: this function takes forever, both due to having to load the file (only once
-- per session though, thanks to the `NOINLINE` pragma,) and actually calculate the path.
-- http://hackage.haskell.org/package/SVGFonts-1.7.0.1/docs/src/Graphics.SVGFonts.Text.html#line-41
-- it is only provided for reference, and not exported. Same for `planetGlyph`
signGlyph :: ZodiacSignName -> Path V2 Double
signGlyph zName =
    textPath unicodeRepr
    where
    unicodeRepr =
        case zName of
            Aries  -> "♈️"
            Taurus -> "♉️"
            Gemini -> "♊️"
            Cancer -> "♋️"
            Leo    -> "♌️"
            Virgo  -> "♍️"
            Libra  -> "♎️"
            Scorpio -> "♏️"
            Sagittarius -> "♐️"
            Capricorn -> "♑️"
            Aquarius -> "♒️"
            Pisces -> "♓️"

planetGlyph :: Planet -> Path V2 Double
planetGlyph p =
    textPath unicodeRepr
    where
        unicodeRepr =
            case p of
                Sun -> "☉"
                Moon -> "☽"
                Mercury -> "☿"
                Venus -> "♀︎"
                Mars -> "♂︎"
                Jupiter -> "♃"
                Saturn -> "♄"
                Uranus -> "♅"
                Neptune -> "♆"
                Pluto -> "♇"
                MeanNode -> "☊"
                _ -> "" -- Earth, Lilith, Chiron, TrueNode, etc.

-- All of these are created by calling `signGlyph` with each sign in a REPL and copy-pasting... 
-- horrible, but efficient: we pay the price of them at compile time.
-- Alternatively, you can just call `textPath "\xsomeUnicode"` for a more general approach to any
-- font-backed glyph.

